insert.if:

update data_tb   
set pvs=pvs+1,
	uvs=uvs+#{uv},
	ips=#{ip}    
where
 article_id = #{Data.articleId}
AND   
acount_id = #{Data.acountId}
AND   
create_date = CURDATE()

mapper.select:
SELECT data_id, pvs, uvs, ips, create_date, article_id, acount_id FROM data_tb
AND data_id=#{dataId}
AND DATE(create_date)=DATE(#{createDate})
AND article_id=#{articleId}
AND acount_id=#{acountId}


    SELECT
	    data_id,
	 	pvs,
	 	uvs,
	 	ips,
    	create_date,
    	article_id,
    	acount_id
	FROM data_tb


	AND DATE(create_date)=DATE(#{createDate})


	AND  article_id=#{articleId}


	AND acount_id=#{acountId}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM data_tb 


	AND DATE(create_date)=DATE(#{createDate})


	AND  article_id=#{articleId}


	AND acount_id=#{acountId}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(data_id)
FROM data_tb


	AND DATE(create_date)=DATE(#{createDate})


	AND  article_id=#{articleId}


	AND acount_id=#{acountId}
