mapper.select:

   SELECT
	    finance_details_id,
	 	type,
	 	money,
	 	type,
    	status,
    	update_date,
    	finance_id
	FROM finance_details_tb


		AND  finance_id=#{financeId}


		AND  type=#{type}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM finance_details_tb


		AND  finance_id=#{financeId}


		AND  type=#{type}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


   SELECT
	 	sum(money)
	FROM finance_details_tb


		AND  type=#{type}


		AND  update_date=#{updateDate}


		AND  status like CONCAT('%','${likeStatus}','%' )


SELECT
 	count(finance_details_id)
FROM finance_details_tb


	AND  finance_id=#{financeId}


	AND  type=#{type}
