mapper.select:

    SELECT
	    img_id,
	 	img_address,
    	number,
    	article_id
	FROM img_tb


	AND  article_id=#{articleId}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM img_tb 


	AND  article_id=#{articleId}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}
